import axios from "axios";
import { movie } from "../components/moviesection";




const getmoviedata=(option:string|undefined)=>{

    const data = axios.get(`http://localhost:3001/${option}`);
    return data;

}

const addtofavourites=(ele:movie)=>{

    axios.post("http://localhost:3001/favourite",ele,{
        headers:{
            'Content-Type': 'application/json'          }
    })

}

export  {getmoviedata,addtofavourites};
