import { useEffect, useState } from "react";
import { Button, Card, Col, Row, Spinner } from "react-bootstrap";
import { useParams } from "react-router-dom";
import  { addtofavourite,deletefromfavourite,getmoviedata } from "../services/movies";
import "../custom css/moviesection.css";
import Ratings from "./ratings";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart,faDumpster} from "@fortawesome/free-solid-svg-icons";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export type movie= {
   actors:[],
   averageRating:number,
   contentRating:string,
   duration:string,
   genres:[],
   id:string,
   imdbRating:string,
   originalTitle:string,
   poster:string,
   posterurl:string,
   ratings:[],
   releaseDate:string,
   storyline:string,
   title:string,
   year:string

}


const Moviesection=()=>{

   const params=useParams();
   const[moviedata,setmoviedata]=useState<movie[]|[]>([]);
   const[loading,setloading]=useState<boolean>(false);
   let temp=params.option;

   useEffect(()=>{
   
       const fetchdata = async()=>{
     
         try
         {
            setloading(true);
            const res= await getmoviedata(params.option);
            if(res.statusText==='OK')
            {
               setmoviedata(res.data);
            }
            
         }
         
         catch(error)
         {

         }
          
         setloading(false);

       }
    
       
         fetchdata();
   },[params]);
    
   const addtofavourites=async(ele:movie)=>{
           
          try
          {
          
          const res = await addtofavourite(ele);

            if(res.statusText==="Created")
            {
               toast.success("Successsfully added to Favourties")
            }
          
          }
          
           catch(error)
           {

           }
           
           
   }

   const deletefromfavourites=async(ele:movie)=>{
    
    try
    {
        const res=await deletefromfavourite(ele.id);
        if(res.statusText==="OK")
        {
          const res= await getmoviedata(params.option);
          setmoviedata(res.data);
          toast("Successfully deleted from favourites")
         
        }

    } 
    catch(error)
    {

    } 

  
   }
    

   return (

     <>
       {
      <>
        
       {
       
       moviedata.length===0 ? <div style={{minHeight:"100vh",display:"flex",justifyContent:"center"}}>
          <h1 style={{color:"grey",marginTop:"20rem",fontSize:""}} >
            No movies found
          </h1>
            
       </div>  :

      <>
      
      <h2 className="moviesectionheader" style={{color:"grey",padding:"10px"}}>Movies</h2>
      <Row  xs={2}  md={4} lg={5} className="g-4">
      {
      
      moviedata.map(ele => 
        
        <Col key={Math.random()*Math.random()} >
          <Card key={ele.id}  style={{textAlign:"center",height:"400px"}}>
            <Card.Img style={{width:"100%",height:"300px",overflow:"hidden"}} variant="top" src={"img/"+ele.poster} />
            <Card.Body>
              <p style={{fontWeight:"bold",}}>{ele.title}</p>
              <Ratings ratingval={ele.ratings}/>
          <>
          {
             temp !== "favourite" ?  <Button style={{border:"none"}} onClick={addtofavourites.bind(null,ele)} variant="safe">Add to Favourites <FontAwesomeIcon style={{color:"red"}} icon={faHeart} /> </Button> :  <Button style={{border:"none"}} onClick={deletefromfavourites.bind(null,ele)} variant="safe">Delete from Favourites <FontAwesomeIcon style={{color:"grey"}} icon={faDumpster} /> </Button>  
          }
          </>

            </Card.Body>
            </Card>
        </Col>
      )}
       
    </Row>
    <ToastContainer autoClose={500} />
   </>
    
    }
   </>
   }
   </>
   )
}

export default Moviesection;
