import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { movie } from "./moviesection";



const Moviedetails=()=>{

    const params=useParams();
    const [moviedetails,setdetails]=useState<movie|null>(null);
    
  

    useEffect(()=>{

        const getmoviedetails=async()=>{
            const res=await axios.get(`http://localhost:3001/${params.moviesection}/${params.id}`)
            setdetails(res.data);
           
    
        }

       
        getmoviedetails();
    },[params])
 

    return <>
    
    {
      

        moviedetails !== null ?   <div className="uppersection" >
       
        <img src={"img/"+moviedetails?.poster} />
  
      </div>
         :  <h1>Empty</h1>
    }
  
    
    </>

}

export default Moviedetails;
