import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";
import { IconProp } from '@fortawesome/fontawesome-svg-core'
import { faSearch } from "@fortawesome/free-solid-svg-icons";



type ratingtype={
    ratings:[]
}


const Showratings=({ratings}:ratingtype)=>{
    
    const[fullstar,setfullstar]=useState(0);
    const[halfstar,sethalfstar]=useState(0);

    {
        let totalratings=ratings.reduce((acc,ele)=>acc+ele,0);
        let ratingvalue = (totalratings*100/(ratings.length*10))/10;
        setfullstar(Math.floor(ratingvalue));
        sethalfstar(Math.round(ratingvalue-fullstar));
       
         
 
    }
    return (
        <>
            {
                Array.from({length :fullstar}).map(ele=>{
                    <FontAwesomeIcon icon = {faSearch as IconProp} />      
                })
            }
            {
                Array.from({length :halfstar},(_,i)=>{
                                 
                })
                
            }
        </>
    )

}

export default Showratings;
