import { useEffect, useState } from "react";
import { Card, Col, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import getmoviedata from "../services/movies";
import "../custom css/moviesection.css";
import Showratings from "./showratings";

type movie= {
   actors:[],
   averageRating:number,
   contentRating:string,
   duration:string,
   genres:[],
   id:string,
   imdbRating:string,
   originalTitle:string,
   poster:string,
   posterurl:string,
   ratings:[],
   releaseDate:string,
   storyline:string,
   title:string,
   year:string

}


const Moviesection=()=>{

   const params=useParams();
   const[moviedata,setmoviedata]=useState<movie[]|[]>([]);


   useEffect(()=>{
        
       const fetchdata = async()=>{
        
         try
         {
            const res= await getmoviedata(params.option);
            if(res.statusText==='OK')
            {
               setmoviedata(res.data);
            }
            
         }
         
         catch(error)
         {

         }
        

       }
    
       
         fetchdata();
   },[]);
    
   return (
      <>
      <h2 className="moviesectionheader" style={{color:"grey",padding:"10px"}}>Movies</h2>
      <Row  xs={2}  md={4} lg={5} className="g-4">
      {
      
      moviedata.map(ele => 
           
        <Col >
          <Card key={ele.id}  style={{textAlign:"center",height:"400px"}}>
            <Card.Img style={{width:"100%",height:"300px",overflow:"hidden"}} variant="top" src={"img/"+ele.poster} />
            <Card.Body>
              <p style={{fontWeight:"bold",}}>{ele.title}</p>
              <Showratings ratings={ele.ratings} />
            </Card.Body>
          </Card>
        </Col>
      )}
    </Row>
      </>
   )
}

export default Moviesection;
