import { useEffect, useState } from "react";
import { Button, Card, Col, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import  { addtofavourites,getmoviedata } from "../services/movies";
import "../custom css/moviesection.css";
import Ratings from "./ratings";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart} from "@fortawesome/free-solid-svg-icons";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export type movie= {
   actors:[],
   averageRating:number,
   contentRating:string,
   duration:string,
   genres:[],
   id:string,
   imdbRating:string,
   originalTitle:string,
   poster:string,
   posterurl:string,
   ratings:[],
   releaseDate:string,
   storyline:string,
   title:string,
   year:string

}


const Moviesection=()=>{

   const params=useParams();
   const[moviedata,setmoviedata]=useState<movie[]|[]>([]);
   

   useEffect(()=>{
   
       const fetchdata = async()=>{
     
         try
         {
            const res= await getmoviedata(params.option);
            if(res.statusText==='OK')
            {
               setmoviedata(res.data);
            }
            
         }
         
         catch(error)
         {

         }
        

       }
    
       
         fetchdata();
   },[params]);
    
   const generatenotification=async(ele:movie)=>{
           
          try
          {
          
          const res = await addtofavourites(ele);

          if(res.statusText==='OK')
            {
              toast("Added to Favourties!");
            }
          
          }
          
           catch(error)
           {

           }
           
           
   }
    

   return (
      <>
      
      <h2 className="moviesectionheader" style={{color:"grey",padding:"10px"}}>Movies</h2>
      <Row  xs={2}  md={4} lg={5} className="g-4">
      {
      
      moviedata.map(ele => 
        
        <Col key={Math.random()*Math.random()} >
          <Card key={ele.id}  style={{textAlign:"center",height:"400px"}}>
            <Card.Img style={{width:"100%",height:"300px",overflow:"hidden"}} variant="top" src={"img/"+ele.poster} />
            <Card.Body>
              <p style={{fontWeight:"bold",}}>{ele.title}</p>
              <Ratings ratingval={ele.ratings}/>
              
            </Card.Body>
            <Button style={{border:"none"}} onClick={generatenotification.bind(null,ele)} variant="safe">Add to Favourites <FontAwesomeIcon style={{color:"red"}} icon={faHeart} /> </Button>      
          </Card>
        </Col>
      )}
       
    </Row>
    <ToastContainer autoClose={500} />
   </>
   )
}

export default Moviesection;
