import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { movie } from "./moviesection";



const Moviedetails=()=>{

    const params=useParams();
    const [moviedetails,setdetails]=useState<movie>();
    const [loading,setloading]=useState<boolean>(false);
    console.log("helllo");
  

    useEffect(()=>{

        const getmoviedetails=async()=>{
            setloading(true);
            const res=await axios.get(`http://localhost:3001/${params.moviesection}/${params.id}`)
            if(res.statusText==='OK')
            {
                setdetails(res.data);
            }
          
           
    
        }

       
        getmoviedetails();
       
    },[params])
 

    return <>
    
    {
      <div>
          <h1>{moviedetails?.title}</h1>
           <img width={"500px"} height={"500px"} alt="" src={"img/"+moviedetails?.poster}/>
       
      </div>
       
    }
  
    
    </>

}

export default Moviedetails;
